MAN.EXAMPLE:
// example #1
x=-10:10; y=-10:10;m =rand(21,21);
xbasc()
xset("colormap",hotcolormap(64))
Sgrayplot(x,y,m, strf="011", rect=[-20,-20,20,20])

// example #2
t=-%pi:0.1:%pi; m=sin(t)'*cos(t);
xbasc()
xset("colormap",jetcolormap(64))
colorbar(-1,1)
Sgrayplot(t,t,m, strf="041")

// example #3: an animation display cos(t)*sin(x)sin(y).
// (Remark: this animation uses the old mode)
n = 30;
nt = 100;
x = linspace(0,2*%pi,n);
y = linspace(0,%pi,n/2);
z = sin(x')*sin(y);
t = linspace(0,4*%pi,nt);
xselect(); xbasc()
fig_styl_init = get("figure_style");
if fig_styl_init == "new" then, set("figure_style","old"), end
xset("font",6,2)
xset("colormap",jetcolormap(64))
driver("X11"); xset("pixmap",1);
for i = 1:nt
   xbasc()
   colorbar(-1,1)
   Sgrayplot(x,y,cos(t(i))*z, strf="042", zminmax=[-1,1])
   xtitle("Kaa''s eyes")
   xset("wshow")
end
xset("pixmap",0); driver("Rec")
if fig_styl_init == "new" then, set("figure_style","new"), end
