set("figure_style","new") //create a figure
a=get("current_axes");
a.title
type(a.title)
plot3d()
a.x_label
a.y_label
a.z_label
xtitle("My title","my x axis label", "Volume") 
a.z_label.text="Month"

t=a.title;
t.foreground=9;
t.font_size=5;
t.font_style=5;
t.text="SCILAB";

xlabel=a.x_label;
xlabel.text=" Weight"
xlabel.font_style= 5;
a.y_label.foreground = 12;
