MAN.EXAMPLE:


set("figure_style","new") //create a figure
a=get("current_axes"); 
a.data_bounds=[-2,-4;2,4];
a.axes_visible="on"; 
a.box="off"; 

xtitle(['Titre';'Principal'],'x','y');
t=a.title;
t.text //display text on console
t.font_size=4;
t.text="A brand new@title string"  
t.font_style=5;
a.x_location="middle"; 
a.y_location="right";
